import React, { Component } from 'react';
import NavBar from './navbar';
import ApplicationCard from './appcard';
import { Card, CardBody, CardTitle, Button, FormGroup, Input, Form} from 'reactstrap';
import axios from 'axios';
import ls from 'local-storage'
import { runInThisContext } from 'vm';

class Applications extends Component {
	constructor(props) {
		super();
		this.handleData = this.handleData.bind(this);
		this.state = {
			data:[], 
			newvalues_degree: 0,
			newvalues_alevels: 0,
			newvalues_languages: 0,
			newvalues_employment: 0,
			newvalues_skills: 0,
			repsonse:null,
			fromChild:'',
			radioValue: 2
		}
	}
	handleData(data) {
		this.setState({
			fromChild:data
		})
	}

	update2degree = (value)=>{
		this.setState({newvalues_degree: value})
	}


	update2alevels = (value)=>{
		this.setState({newvalues_alevels: value})
	}

	update2languages = (value)=>{
		this.setState({newvalues_languages: value})
	}

	update2employment = (value)=>{
		this.setState({newvalues_employment: value})
	}

	update2skills = (value)=>{
		this.setState({newvalues_skills: value})
	}

	getRadio = (value)=>{
		if (value =="accept") {
			this.setState({radioValue: 2})
		} else if (value == "test") {
			this.setState({radioValue: 1})
		} else {
			this.setState({radioValue: 0})
		}

	}

	componentDidMount() {
		axios.get("https://jsonplaceholder.typicode.com/todos/")
		.then(res => {
			const jobs = res.data;
			const test = [{"Name": "Nathan Bailey", "DegreeQualification": "x", "DegreeLevel": "x", "UniversityAttended": "x", "ALevelQualifications": [{"Subject": "Engineering ", "Grade": "B"}, {"Subject": "Portuguese ", "Grade": "B"}, {"Subject": "Japanese ", "Grade": "B"}, {"Subject": "Food Technology ", "Grade": "B"}], "LanguagesKnown": [{"Language": "C#", "Expertise": 4}, {"Language": "C++", "Expertise": 0}, {"Language": "Java", "Expertise": 2}], "PreviousEmployment": [{"Company": "Shoreline", "Position": "Module Lead", "Length of Employment": "1 year 9 months"}, {"Company": "Gekko and Co.", "Position": "software developer", "Length of Employment": "1 year 9 months"}], "Skills": [{"Skill": "Public Speaking", "Expertise": 3}, {"Skill": "Motivated", "Expertise": 6}, {"Skill": "Search engine optimization", "Expertise": 7}, {"Skill": "Text Editing", "Expertise": 1}, {"Skill": "Quality Assurance", "Expertise": 1}, {"Skill": "Skype", "Expertise": 7}, {"Skill": "Video Editing", "Expertise": 2}], "Hobbies": [], "Response": "x", "score_degree": 3.0, "score_alevels": 4.0, "score_lang": 5.0, "score_employment": 6.0, "score_skills": 7.0, "ID": 1}]
			this.setState({data:test})
		})
	}

	onSubmit(event) {
		var json = [{"Degree": this.state.newvalues_degree}, {"ALevels": this.state.newvalues_alevels}, {"Languages": this.state.newvalues_languages}, {"Employment": this.state.newvalues_languages}, {"Skills": this.state.newvalues_skills}, {"Response": this.state.radioValue}]
		console.log(json)
		event.preventDefault();
		console.log("HI");
		const data = event.target;
		console.log(data);
		const newtest = [];
		this.setState({data:newtest})
	}

	handlerfordata=(data)=>{
		console.log("in handler")
	}


	render() {
		let filter = {
			margin: '15px',
			marginLeft: '40px',
			fontFamily: 'Quicksand',
			width: '25%',
			height: '100vh',
			float: 'left',
		}
		let jobs = {
			margin: '15px',
			fontFamily: 'Quicksand',
			width: '68%',
			height: '100vh',
			float: 'right',
		}


		const application_length = this.state.data.length;
		return (
			<div>
				<NavBar />
				<div style={{height: '100vh'}}>
					<div style={filter}>
						<h5>Filter</h5>
					</div>
					<div style={jobs}>
						<h4>Applications</h4>
						<p>{application_length} result(s)</p>
						{this.state.data.map(t =>

						<Form onSubmit = {this.onSubmit.bind(this)}>
						<ApplicationCard updatedegree={this.update2degree} updateskills={this.update2skills} updatealevels={this.update2alevels} updateemployment={this.update2employment} updatelanguages = {this.update2languages} getRadioValue = {this.getRadio} values = {[t.score_degree, t.score_alevels, t.score_lang, t.score_employment, t.score_skills]}>

						<h6>Degree</h6>
							<ul>
								<li>Degree: {t.DegreeQualification}</li>
								<li>Degree Level: {t.DegreeLevel}</li>
								<li>University Attended: {t.UniversityAttended}</li>
							</ul>
							<h6>A-Levels</h6>
							<ul>
							{t.ALevelQualifications.map(x=>
								<li>Subject: {x.Subject}. Grade: {x.Grade}</li>
							)}
							</ul>
							<h6>Langauges</h6>
							<ul>
							{t.LanguagesKnown.map(x=>
								<li>Language: {x.Language}. Expertise: {x.Expertise}</li>
							)}
							</ul>
							<h6>Previous Employment</h6>
							<ul>
							{t.PreviousEmployment.map(x=>
								<li>Company: {x.Company}. Position: {x.Position}</li>
							)}
							</ul>
							<h6>Skills</h6>
							<ul>
							{t.Skills.map(x=>
								<li>Skill: {x.Skill}. Expertise: {x.Expertise}</li>
							)}
							</ul>
							<div style={{textAlign: 'center'}}>
						<Button color="info">Submit</Button>
						</div>			
						</ApplicationCard>
						</Form>
						)}
					</div>
				</div>
			</div>
		);
	}
}

export default Applications;
